                        $("#m_serviceCharge").html("￥" + parseFloat(result.Data.ServiceCharge).toFixed(2));
                        unpaidMoney=parseFloat(result.Data.TotalPrice).toFixed(2);
                        if (result.Data.Discount < 0)
                            result.Data.Discount = 0;

                        $("#m_discount").html("￥" + parseFloat(result.Data.Discount).toFixed(2));
                        $("p[flag='data']").remove();
                       
                        if (result.Data.Items != null) {
                            $.each(result.Data.Items, function(a, b) {
                                var temp = $("#infoTemp").clone().show().attr("flag", "data");
                                temp.find("em[field='m_ticketName']").html(b.TicketName);
                                temp.find("em[field='m_price']").html("￥" + parseFloat(b.Price).toFixed(2));
                                temp.find("em[field='m_count']").html(b.Count);
                                $("#ticketflag").before(temp);
                            });
                        } else {
                            var temp = $("#infoTemp").clone().show().attr("flag", "data");
