        // 修改手机号
        function modifyMobile(bt) {
            var txtMobile = $("#txtMobile");
            var spanMobile = $("#spanMobile");
            if (txtMobile.css("display") == "none") {
                txtMobile.val(spanMobile.html());
                $(bt).html("[保存]");
            } else {
                var newMobile = txtMobile.val();
                if (/^1[34]\d{9}$/g.test(newMobile) || (/^15[0-35-9]\d{8}$/g.test(newMobile)) || (/^18[05-9]\d{8}$/g.test(newMobile))) {
                    if (spanMobile.html() !== newMobile) {
                        $.ajax({
                            async: true,
                            //timeout: 30000,
                            type: "POST",
                            cache: false,
                            url: document.forms[0].action,
                            data: { phone: newMobile },
                            beforeSend: function() {
                                Processing();
                            }
                        })
                            .done(function(msg) {
                                var rs = eval("(" + msg + ")");
                                if (rs.result === 0) {
                                    spanMobile.html(newMobile)
                                } else {
                                    alert(rs.msg);
                                }
                            })
                            .fail(function(jqXHR, textStatus) {
                                alert("网络异常!");
                            })
                            .always(function() {
                                Complete();
                            });

                    }
                } else {
                    alert("不是完整的11位手机号或者正确的手机号前七位");
                    return;
                }
                $(bt).html("[修改]");
            }
            txtMobile.toggle();
        }

