        function GetCouponTicket() {
            if (coupons) {
                var tab = $("#tab_couponTicket");
                var totalPrice = 0;
                var itemPrice = 0;
                $.each(coupons, function(i, item) {
                    totalPrice += item.TotalPrice;
                    itemPrice = item.ItemPrice;
                    var str = "<li class=\"cf\" status=\"3\" mapindex=\"1\"><em>" + item.CouponNo + "</em><em class=\"status\">" + item.Type + "</em><em>已支付</em> </li>";
                    tab.append(str);
                });
                if (coupons.length > 0 && unpaidMoney > 0) {
                    if (unpaiedCount < 1) unpaiedCount = count;
                    var unionPay = parseFloat(unpaidMoney + unpaiedCount * unionCharge).toFixed(2);
                    var alipayMoney = parseFloat(unpaidMoney + unpaiedCount * alipayCharge).toFixed(2);
                    var weinxinPay = parseFloat(unpaidMoney + unpaiedCount * weixinCharge).toFixed(2);
                    if (unionPay < 0) unionPay = 0;
                    if (alipayMoney < 0) alipayMoney = 0;
                    if(weinxinPay<0) weinxinPay=0;
                    var result = "您已使用了<em class='red'>" + coupons.length + "</em>张电子券，银联还需支付<em class='red'>￥" + unionPay + "</em>元，支付宝还需支付<em class='red'>￥" + alipayMoney + "</em>元，微信还需支付<em class='red'>￥"+weinxinPay+"</em>元";
                    $("#checkResult").html(result);
                }
            }
        }

