<script type="text/javascript">
    var imgUrl = '../../img.cfc.com.cn_3A6600';
    var filmNo = QueryString('fno');
    var type;

    $(function() {

        type = QueryString('type');
        if (type != null) {
            $("#a_buy").hide();
        }

        //加载影片剧照
        InitFilmPicture();
//        
//        arrimg = $("#div_FilmPicture img");
//        for (var i = 0; i < arrimg.length; i++) {
//            arrimg[i].index = i;
//            arrimg[i].onclick = function() {
//                imgShow(this.index);
//            }
//        }
        
        $('#div_FilmPicture a').lightBox();


        $(".txtComment").focus(function() {
            if (this.value == "在此尽情抒发你的情感吧")
                this.value = "";
            checkIsLogin();

        });
        $(".txtComment").blur(function() {
            if (this.value == "")
                this.value = "在此尽情抒发你的情感吧";
        });



    });
    
    //加载影片剧照
    function InitFilmPicture() {
        $.ajax({
            type: "POST",
            dataType: "json",
            url: "GetFilmData.ashx",      //提交到一般处理程序请求数据
            data: "filmno=" + $("#ContentPlaceHolder1_hidFilmNo").val() + "&datatype=filmpicture",
            async: false,
            success: function(data) {
                var twos = "";
                if (data != null) {
                    var rowsData = data;
                    $.each(rowsData, function(i, n) {
                    twos += "<a href='" + imgUrl + n.PictureUrl + "'><img src='" + imgUrl + n.PictureUrl + "' width='181' height='125' /></a>";  //onclick='imgShow(this)'                
                    });

                }
                else {
                    twos += "<div>温馨提示：该影片暂无剧照！</div>";
                }
                $("#div_FilmPicture").html(twos);

            }
        });
    }

    function showComment() {
        $(".commit").show();

    }
    function hideComment() {
        $(".commit").hide();
        $("#div_errmsg").hide();

    }

    function checkcomment() {
        var chkbol = checkIsLogin();
        if (chkbol) {
            var commtxt = $(".txtComment").val();
            if (commtxt == null || commtxt == "" || commtxt == "在此尽情抒发你的情感吧") {
                $("#lblerrmsg").text("温馨提示：评论内容不能为空！");
                $("#div_errmsg").show();
                return false;
            }
            else if (commtxt.length > 140) {
            $("#lblerrmsg").text("温馨提示：评论内容不能超过140个字！");
            $("#div_errmsg").show();
                return false;
            }
            else {
                $("#div_errmsg").hide();
                return true;
            }
        }        
        return false;
             
    }
    
    function reloadpage() {
    if (type != null) {
        window.location.href = 'detail.aspx@type=0&fno=' + filmNo;
    }
    else{
    window.location.href = 'detail.aspx@fno=' + filmNo;
    }
        }

    
</script>




        
        

